import React from 'react';
import './App.css';
import PokemonList from './components/PokemonList';
import { useAppSelector } from './hooks';
import PokemonDetails from './components/PokemonDetails';
import { setSelectedPokemon } from './features/pokemonSlice';
import { useDispatch } from 'react-redux';

function App() {
  const dispatch = useDispatch();
  const selectedPokemon = useAppSelector((state) => state.pokemon.selectedPokemon);

  return (
    <div>
      <div className='pokemonlist__header' onClick={() => {dispatch(setSelectedPokemon(null))}}>
        <p className='pokemonlist__header__title'>{selectedPokemon ? selectedPokemon : 'PokeReact'}</p>
      </div>
      {selectedPokemon ? <PokemonDetails /> : <PokemonList />}
    </div>
  );
}

export default App;
